import React, { useState } from 'react';
import './App.css';
import '@fortawesome/fontawesome-free/css/all.min.css'; // Import FontAwesome

function App() {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');


  const handleSubmit = async (e) => {
    e.preventDefault();

    // Make a POST request to the backend API with the URL in the body
    try {
      const response = await fetch('http://localhost:5000/classify', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json', // Set correct headers
        },
        body: JSON.stringify({ url }), // Send the url in the request body
      });

      const data = await response.json(); // Parse the response as JSON

      // Check if the response contains the result and update the UI
      if (response.ok) {
        setResult(data.result); // Set result in the frontend based on backend response
      } else {
        setResult('Error in classification');
        console.error('Error:', data);
      }
    } catch (error) {
      setResult('Error in classification');
      console.error('Request error:', error);
    }
  };


  // const handleSubmit = async (e) => {
  //   e.preventDefault();

  //   try {
  //     const response = await fetch('http://localhost:5000/classify', {
  //       method: 'POST',
  //       headers: {
  //         'Content-Type': 'application/json',
  //       },
  //       body: JSON.stringify({ url }),
  //     });

  //     const data = await response.json();

  //     if (response.ok) {
  //       setResult(data.result);
  //     } else {
  //       setError('Error in classification');
  //     }
  //   } catch (err) {
  //     console.error('Error:', err);
  //     setError('Error connecting to the server');
  //   }
  // };

  return (
    <div className="App">
      <header className="App-header">
        <h1><i className="fas fa-shield-alt"></i> Phishing URL Classifier</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Enter URL"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
          <button type="submit">Detect</button>
        </form>

        {error && <p className="error-message">{error}</p>}

        {result && (
          <p className={result === 'Phishing' ? 'phishing-result' : 'not-phishing-result'}>
            {result === 'Phishing' && <i className="fas fa-exclamation-triangle blinking-icon"></i>}
            Result: {result}
          </p>
        )}
      </header>

      <div className="footer">
        University of Ruhuna | Faculty of Engineering | Undergraduate Project | Group 06
      </div>
    </div>
  );
}

export default App;
