import React, { useState } from 'react';
import './App.css';
import '@fortawesome/fontawesome-free/css/all.min.css'; // Import FontAwesome

function App() {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/classify', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }), // Send the URL in the request body
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setResult(data.result); // Set the result from the backend response

    } catch (error) {
      console.error('Error:', error);
      setResult('Error in classification'); // Handle errors gracefully
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1><i className="fas fa-shield-alt"></i> Phishing URL Classifier</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Enter URL"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
          <button type="submit">Detect</button>
        </form>

        {result && (
          <p className={result === 'Phishing' ? 'phishing-result' : 'not-phishing-result'}>
            {result === 'Phishing' && <i className="fas fa-exclamation-triangle blinking-icon"></i>}
            Result: {result}
          </p>
        )}
      </header>

      <div className="footer">
        University of Ruhuna | Faculty of Engineering | Undergraduate Project | Group 06
      </div>
    </div>
  );
}

export default App;
